    <script type="text/javascript">
        $(document).ready(function(){
            $('.point').click(function(){
                $('.point.active, .history-text').removeClass('active');
                $(this).addClass('active');
                $('.history-text').eq($(this).index()).addClass('active');
            });

            $('.economic, .ambiental').click(function(){
                $('.impacts .left > div ').html($(this).children('.text').clone());
            });

        });
    </script>
